void cleanup();
int initialize();
void run();

int main(void){

    if(initialize()){
        printf("Initialize Failed\n");
    }

    run();
    FMAP0 = (int*)fmap_data;
    cleanup();
    return 0;
}
int initialize(){

    char* Plt = "Altera";

    //Get PlatformID
    platform = find_Platform(Plt);

    //Get DeviceID
    //
    err = clGetDeviceIDs(platform, CL_DEVICE_TYPE_ALL, 1, &device, NULL);
    checkerror(err,"Error: Failed to get DeviceID!");
    // Create a compute context
    //
    context = clCreateContext(NULL, 1, &device, NULL, NULL, &err);
    checkerror(err,"Error: Failed to Create context!");
    // Create a command queue
    //
    queue[0] = clCreateCommandQueue(context, device, CL_QUEUE_PROFILING_ENABLE, &err);
    checkerror(err,"Error: Failed to create a command queue[0]!");

    string binary_file = getBoardBinaryFile("kernel_e", device);
    printf("Using AOCX: %s\n", binary_file.c_str());
    program = createProgramFromBinary(context, binary_file.c_str(), &device, 1);

    if (!program)
    {
        printf("Error: Failed to create compute program!\n");
        return EXIT_FAILURE;

    }

    // Build the program executable
    //
    err = clBuildProgram(program, 0, NULL, NULL, NULL, NULL);
    if (err != CL_SUCCESS)
    {
        size_t len;
        char buffer[4096];
        printf("Error: Failed to build program executable!\n");
        clGetProgramBuildInfo(program, device, CL_PROGRAM_BUILD_LOG, sizeof(buffer), buffer, &len);
        printf("%s\n", buffer);
        exit(1);
    }

    // Create the compute kernel in the program we wish to run
    //
    kernel[0] = clCreateKernel(program, "test", &err);
    checkerror(err,"Error: Failed to create compute kernel[0]!");

    // Create the input and output arrays in device memory for our calculation
    //
    x = clCreateBuffer(context, CL_MEM_WRITE_ONLY, sizeof(int) * 3 * 34 * 34, NULL, NULL);

    fmap0 = clCreateBuffer(context, CL_MEM_READ_ONLY, sizeof(int) * 3 * 34 * 34, NULL, NULL);

    return 0;
}

void run(){


    // Write our data set into the input array in device memory
    //
    err = clEnqueueWriteBuffer(queue[0], fmap0, CL_FALSE, 0, sizeof(int) * 3 * 34 * 34, FMAP0, 0, NULL, NULL);

    // Set the arguments to our compute kernel
    //

    err = clSetKernelArg(kernel[0], 0, sizeof(cl_mem), &fmap0);
    err = clSetKernelArg(kernel[0], 1, sizeof(cl_mem), &x);
    checkerror(err,"Error: Failed to set kernel arguments! - kernel[0]");

    global = {3,34,34};
    err = clEnqueueNDRangeKernel(queue[0], kernel[0], 3, NULL, global, NULL, 0, NULL, NULL);
    checkerror(err,"Error: Failed to execute kernel[0]");

    clFinish(queue[0]);
    //clFinish(queue[1]);
    err = clEnqueueReadBuffer( queue[0] , x, CL_TRUE, 0, sizeof(int) * 3 * 34 * 34, (void *) X1, 0, NULL, NULL );
    printf("Complete ");

    correct=0;
    int count=0;
    int flag=0;

    for(unsigned char i = 0; i < 3; i++){
        for(unsigned char j = 0; j < 34; j++){
            for(unsigned char k = 0; k < 34; k++){
                count++;
                printf("Expected = %d  Optained = %d\n",fmap0_test[i][j][k], X1[(j * 34) + k + (i * (34*34))]);

                if(fmap0_test[i][j][k] == X1[(j * 34) + k + (i * (34*34))]){
                    //printf("Expected = %d  Optained = %d\n",fmap0_test[i][j][k], X1[(j * 34) + k + (i * (34*34))]);
                    correct++;
                }
                else{
                    //printf("Expected = %d  Optained = %d\n",fmap0_test[i][j][k], X1[(j * 34) + k + (i * (34*34))]);
                    flag++;
                }
            }
        }
        if(flag > 0)
            printf("Matrix %d - %d\n",i,flag);
        flag=0;
    }

    printf("\nNo. of Data Correct for fmap0  %d / %d\n",correct,count);
}
void cleanup(){
    clReleaseMemObject(x);
    clReleaseMemObject(fmap0);
    clReleaseProgram(program);
    clReleaseKernel(kernel[0]);
    clReleaseCommandQueue(queue[0]);
    clReleaseContext(context);
    free(X1);
    //free(FMAP0);

}
